import React from 'react';
import { Container, Form } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import { Button } from 'react-bootstrap';

const Auth = () => {
	return (
		<Container 
			className="d-flex justify-content-center align-items-center"
			style={{height: window.innerHeight - 54}}
		>
			<Card style={{width: 520, height: 335}} className="p-3 border-light shadow">
				<h2 className="m-auto">Авторізація</h2>
				<Form className="d-flex flex-column">
					<Form.Control
						style={{height: 50}}
						className="mt-4"
						placeholder="Email"
					/>
					<Form.Control
						style={{height: 50}}
						className="mt-4"
						placeholder="Password"
					/>
					<Button 
						// style={{borderRadius: 43, height: 45, background: "#00B207", border: "none"}} 
						className="rounded-cicle mt-4" 
						variant={"btn-outline-success"}
					>
						Увійти
					</Button>
				</Form>	
			</Card>
			
		</Container>
	);
};

export default Auth;
