import React from 'react';
import { Container, Form } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import { Button } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { REGISTRATION_ROUTE } from '../utils/const';

const Auth = () => {
	return (
		<Container 
			className="d-flex justify-content-center align-items-center"
			style={{height: window.innerHeight - 54}}
		>
			<Card style={{width: 520, height: 335}} className="p-3 border-light shadow">
				<h2 className="m-auto">Авторізація</h2>
				<Form className="d-flex flex-column">
					<Form.Control
						style={{height: 50}}
						className="mt-3"
						placeholder="Email"
					/>
					<Form.Control
						style={{height: 50}}
						className="mt-3"
						placeholder="Password"
					/>
					<Button 
						style={{borderRadius: 43, height: 45}} 
						className="rounded-cicle mt-4" 
						variant={"outline-success"}
					>
						Увійти
					</Button>
					<Row 
						className="d-flex justify-content-between"
					>
						<div>
							Немає аккаунта? <NavLink to={REGISTRATION_ROUTE}>Реєстрація</NavLink>
						</div>
					</Row>
				</Form>	
			</Card>
			
		</Container>
	);
};

export default Auth;
