import React from 'react';
import { Col, Container, Image, Row } from 'react-bootstrap';

const DevicePage = () => {
	const device = {id: 1, name: "Таврія", price: 120000, rating: 5, img: `https://traktorist.ua/media/uploads/my3PscCmT-ZAZ_Tavria_Jilemnice_2.jpg`}
	return (
		<Container className='mt-3'>
			<Col md={4}>
				<Row>
					<h2
						className='ml-4'
						style={{
							fontWeight: '600',
							fontSize: '32px'
						}}
					>
						{device.name}
					</h2>
				</Row>
			</Col>
			<Col md={4}>
				<Image 
					src={device.img}
					style={{
						paddingTop: '50px',
						width: '500px',
						height: '500px',
						objectFit: 'cover',
						borderRadius: '10px'
					}}
				/>
			</Col>
			<Col md={4}>
				
			</Col>
		</Container>
	);
};

export default DevicePage;
