import React from 'react';
import { Col, Container, Image, Row, Card } from 'react-bootstrap';

const DevicePage = () => {
	const device = {id: 1, name: "Таврія", price: 120000, rating: 5, img: `https://traktorist.ua/media/uploads/my3PscCmT-ZAZ_Tavria_Jilemnice_2.jpg`}
	return (
		<Container className='mt-3'>
			<Col md={6}>
				<Row>
					<h2
						className='ml-5 mt-5'
						style={{
							fontWeight: '600',
							fontSize: '32px'
						}}
					>
						{device.name}
					</h2>
				</Row>
			</Col>
			<Col md={6}>
				<Image 
					src={device.img}
					style={{
						marginTop: '20px',
						width: '100%',
						height: '525px',
						objectFit: 'cover',
						borderRadius: '10px'
					}}
				/>
			</Col>
			<Col md={6}>
				<Row 
					className='d-flex justify-content-center align-items-center'
				>
					<div
						style={{
							paddingTop: '18px',
							fontSize: '18px',
							color: '#B3B3B3'
						}}
					>
						Ціна з ПДВ:
					</div>
					<h3 
						style={{
							marginBottom: '0',
							marginLeft: '20px',
							paddingTop: '10px',
							fontSize: '30px',
							fontWeight: '500'
						}}
					>
						{device.price} грн
					</h3>
				</Row>
			</Col>
		</Container>
	);
};

export default DevicePage;
