import React from 'react';
import { Container, Form } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import { Button } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { NavLink, useLocation } from 'react-router-dom';
import { LOGIN_ROUTE, REGISTRATION_ROUTE } from '../utils/const';

const Auth = () => {
	const location = useLocation()
	const isLogin = location.pathname === LOGIN_ROUTE


	return (
		<Container 
			className="d-flex justify-content-center align-items-center"
			style={{height: window.innerHeight - 54}}
		>
			<Card style={{width: 520, height: 335}} className="p-3 border-light shadow">
				<h2 className="m-auto">{isLogin ? 'Авторізація' : "Реєстрація"}</h2>
				<Form className="d-flex flex-column">
					<Form.Control
						style={{height: 50}}
						className="mt-3"
						placeholder="Email"
					/>
					<Form.Control
						style={{height: 50}}
						className="mt-3"
						placeholder="Password"
					/>
					<Button 
						style={{borderRadius: 43, height: 45}} 
						className="mt-4" 
						variant={"outline-success"}
					>
						{isLogin ? 'Увійти' : 'Реєстрація'}
					</Button>
					<Row 
						className="d-flex justify-content-center mt-3 mb-2"
					>
						{isLogin ?
							<div>
								Немає аккаунта? <NavLink to={REGISTRATION_ROUTE}>Реєстрація</NavLink>
							</div>
							:
							<div>
								Вже є аккаунт? <NavLink to={LOGIN_ROUTE}>Увійти</NavLink>
							</div>
						}
					</Row>
				</Form>	
			</Card>
			
		</Container>
	);
};

export default Auth;
