import React from 'react';
import { Col, Container, Image, Row } from 'react-bootstrap';

const DevicePage = () => {
	const device = {id: 1, name: "Таврія", price: 120000, rating: 5, img: `https://traktorist.ua/media/uploads/my3PscCmT-ZAZ_Tavria_Jilemnice_2.jpg`}
	const description = [
		{id: 1, title: 'Тип вентилятора', description: 'Вентилятор пиловий'},
		{id: 2, title: 'Номер вентилятора', description: '№4'},
		{id: 3, title: 'Матеріал вентилятора', description: 'вуглецева сталь'},
		{id: 4, title: 'Конструктивне виконання', description: '5'}
	]
	return (
		<Container className='mt-3'>
			<Row>
				<Col md={6}>
					<Row>
						<h2
							className='ml-5 mt-5'
							style={{
								fontWeight: '600',
								fontSize: '32px'
							}}
						>
							{device.name}
						</h2>
						<Image 
							src={device.img}
							style={{
								marginTop: '20px',
								width: '100%',
								height: '525px',
								objectFit: 'cover',
								borderRadius: '10px'
							}}
						/>
						<div className='d-flex justify-content-center align-items-center'
							style={{marginLeft: '25%'}}
						>
							<div
								style={{
									paddingTop: '18px',
									fontSize: '18px',
									color: '#B3B3B3'
								}}
							>
								Ціна з ПДВ:
							</div>
							<h3 
								style={{
									marginBottom: '0',
									marginLeft: '20px',
									paddingTop: '10px',
									fontSize: '30px',
									fontWeight: '500'
								}}
							>
								{device.price} грн
							</h3>
						</div>
					</Row>
				</Col>
				<Col 
					md={6}
					style={{
						paddingTop: '150px',
						paddingLeft: '150px'
					}}
				>
					<h2 
						style={{fontSize: '32px', fontWeight: '700', marginLeft: '10px', marginBottom: '25px'}}
					>Характеристики:</h2>
					{description.map(info =>
						<Row
							key={info.id}
							style={{
								fontSize: '18px',
								marginTop: '10px'
							}}
						>
							<span style={{ fontWeight: '500' }}>{info.title}:</span> {info.description}
						</Row>
					)}
				</Col>
			</Row>
		</Container>
	);
};

export default DevicePage;
