import React from 'react';
import { Card, Col} from 'react-bootstrap';
import Image from 'react-bootstrap/Image';

const DeviceItem = ({device}) => {
	return (
		<Col md={3} style={{ marginTop: '12px' }}>
			<Card
				style={{
					width: 200,
					cursor: 'pointer',
					boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Лёгкая тень для красоты
					borderRadius: '10px' // Закруглённые углы
				}}
			>
				<Image
					width={200}
					height={200}
					src={device.img}
					style={{
						borderTopLeftRadius: '10px',
						borderTopRightRadius: '10px'
					}} // Закругляем углы изображения, чтобы соответствовали карточке
				/>
				<div className="d-flex justify-content-between align-items-center" style={{ padding: '10px' }}>
				<div>Вентилятори</div>
				<div></div>
				</div>
			</Card>
		</Col>
	  );
};

export default DeviceItem;
