import React, { useContext } from 'react';
import { Context } from '../index';
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { NavLink } from 'react-router-dom';
import { SHOP_ROUTE } from '../utils/const';
import { Button } from 'react-bootstrap';
import { observer } from 'mobx-react-lite';
import Container from 'react-bootstrap/Container';
import '../custom.css';

const NavBar = observer(() => {
	const {user} = useContext(Context)
	return (
		<Navbar bg="dark" variant="dark">
			<Container>
				<NavLink style={{color:'#B3B3B3'}} to={SHOP_ROUTE}>Система автоматизованого підбору промислового обладнання</NavLink>
				{user.isAuth ?
					<Nav className="ml-auto" style={{color:'#B3B3B3'}}>
						<Button variant={"outline-light"} className="custom-outline-light-hover">Адмін панель</Button>
						<Button variant={"outline-light"} className='ml-2 custom-outline-light-hover'>Вийти</Button>
					</Nav>
					:
					<Nav className="ml-auto" style={{color:'#B3B3B3'}}>
						<Button 
							variant={"outline-light"} 
							onClick={() => user.setIsAuth(true)}
							className="custom-outline-light-hover"
						>
							Авторізація
						</Button>
					</Nav>
				}
			</Container>
		</Navbar>
	);
});


export default NavBar;
