import {makeAutoObservable} from "mobx";

export default class DeviceStore { // викроист. mobx
	constructor() {
		this._types = [
			{id: 1, name: "Машинобудування"},
			{id: 2, name: "Конвеєри"}
		]
		this._brands = [
			{id: 1, name: "ЗАЗ"},
			{id: 2, name: "Горизонт"}
		]
		this._devices = [
			{id: 1, name: "Таврія", price: 120000, rating: 5, img: `https://traktorist.ua/media/uploads/my3PscCmT-ZAZ_Tavria_Jilemnice_2.jpg`},
			{id: 2, name: "Таврія", price: 120000, rating: 5, img: `https://traktorist.ua/media/uploads/my3PscCmT-ZAZ_Tavria_Jilemnice_2.jpg`},
			{id: 3, name: "Таврія", price: 120000, rating: 5, img: `https://traktorist.ua/media/uploads/my3PscCmT-ZAZ_Tavria_Jilemnice_2.jpg`},
			{id: 4, name: "Таврія", price: 120000, rating: 5, img: `https://traktorist.ua/media/uploads/my3PscCmT-ZAZ_Tavria_Jilemnice_2.jpg`}
		]
		makeAutoObservable(this)
	}

	setTypes(types) {
		this._types = types
	}
	setBrands(brands) {
		this._brands = brands
	}
	setDevices(devices) {
		this._devices = devices
	}

	get types() {
		return this._types
	}
	get brands() {
		return this._brands
	}
	get devices() {
		return this._devices
	}
}
